"use strict";
document.write("<h2>Display name in browser using typescript dynamically</h2>");
let Name = prompt("Enter your Name :");
document.write(`<h3> Name is : ${Name}`);
document.body.style.background = "radial-gradient(circle, rgba(69,156,137,1) 51%, rgba(127,144,74,1) 100%)";
document.body.style.minHeight = "100vh";
